query:
query Products(\$destinations: [InputCoordinate!]!, \$includeClassificationFilters: Boolean = false, \$pickup: InputCoordinate!, \$pickupTimeMsec: Float, \$targetProductType: EnumRVWebCommonTargetProductType) {
  products(
    destinations: \$destinations
    includeClassificationFilters: \$includeClassificationFilters
    pickup: \$pickup
    pickupTimeMsec: \$pickupTimeMsec
    targetProductType: \$targetProductType
  ) {
    ...ProductsFragment
    __typename
  }
}

fragment ProductsFragment on RVWebCommonProductsResponse {
  classificationFilters {
    ...ClassificationFiltersFragment
    __typename
  }
  defaultVVID
  productsUnavailableMessage
  renderRankingInformation
  tiers {
    ...TierFragment
    __typename
  }
  __typename
}

fragment ClassificationFiltersFragment on RVWebCommonClassificationFilters {
  filters {
    ...ClassificationFilterFragment
    __typename
  }
  hiddenVVIDs
  standardProductVVID
  __typename
}

fragment ClassificationFilterFragment on RVWebCommonClassificationFilter {
  currencyCode
  displayText
  fareDifference
  icon
  vvid
  __typename
}

fragment TierFragment on RVWebCommonProductTier {
  products {
    ...ProductFragment
    __typename
  }
  title
  __typename
}

fragment ProductFragment on RVWebCommonProduct {
  capacity
  cityID
  currencyCode
  description
  detailedDescription
  discountPrimary
  displayName
  estimatedTripTime
  etaStringShort
  fare
  hasFareBreakdownDisclaimer
  hasPromo
  hasRidePass
  id
  isAvailable
  meta
  preAdjustmentValue
  productImageUrl
  productUuid
  reserveEnabled
  __typename
}
